package com.xmy.springbootvuebookdemo.controller;import com.xmy.springbootvuebookdemo.entity.Book;import com.xmy.springbootvuebookdemo.service.BookService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RestController;import java.util.List;/** * @author xmy * @date 2021/4/25 6:07 下午 */@RestControllerpublic class BookController {    @Autowired    BookService bookService;    @GetMapping("/book")    public List<Book> getBook() {        List<Book> bookList = bookService.getAllBooks();        return bookList;    }}